import React, { useState, useContext } from "react"
import AddUserForm from "./AddUserForm"
import { DisplayContext } from "../context/DisplayContext"
import AddLocationForm from "./AddLocationForm"
import { Main, ButtonAction } from "../StyledComponents"

const Settings = () => {
	const { formState, setFormState } = useContext(DisplayContext)

	const [showUser, setShowUser] = useState(true)

	const handleClick = () => {
		setShowUser(!showUser)
	}
	return (
		<Main>
			{showUser ? (
				<>
					<AddUserForm />{" "}
					<ButtonAction onClick={handleClick}>
						Add Location
					</ButtonAction>
				</>
			) : (
				<>
					{" "}
					<AddLocationForm />{" "}
					<ButtonAction onClick={handleClick}>Add User</ButtonAction>
				</>
			)}{" "}
			<ButtonAction onClick={() => setFormState("status")}>
				Back Home
			</ButtonAction>
		</Main>
	)
}
export default Settings
